-- ----------------------------
-- Table structure for type_details
-- ----------------------------
DROP TABLE IF EXISTS `type_details`;
CREATE TABLE `type_details` (
  `id` int NOT NULL AUTO_INCREMENT,
  `animal_name` varchar(50) NOT NULL COMMENT '动物名称',
  `content` text NOT NULL COMMENT '概述',
  `image_url` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '上传图片的地址',
  `type_id` int NOT NULL COMMENT '类别id',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=123465 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of type_details
