INSERT INTO `type_details` VALUES ('123456', '大熊猫', '<p><img src=\"http://localhost:8081/animal/upload/image/20200901/1598899066797048620.png\" title=\"1598899066797048620.png\" alt=\"1-1.png\"/></p><h3>大熊猫</h3><p><strong><span style=\"font-family: 宋体; font-size: 14px; font-weight: 700;\">学名：</span></strong><span style=\"font-family: 宋体; font-size: 14px; font-weight: 700;\">Ailuiopodidae melanoleuca</span></p><p><strong style=\"margin: 0px; padding: 0px; font-family: 宋体; font-size: 14px; text-indent: 30px; white-space: normal;\">别名：</strong><span style=\"font-family: 宋体; font-size: 14px; font-weight: 700;\">花猫，花熊，华熊，竹熊，花头熊,，银狗，大浣熊，<span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">我国台</span><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">湾地区也被称为猫熊。</span></span></p><p><span style=\"font-family: 宋体; font-size: 14px; font-weight: 700;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\"><strong style=\"margin: 0px; padding: 0px; font-family: 宋体; font-size: 14px; text-indent: 30px; white-space: normal;\">英文名：</strong><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">Giant Panda</span></span></span></p><p><span style=\"font-family: 宋体; font-size: 14px; font-weight: 700;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\"><strong style=\"margin: 0px; padding: 0px; font-family: 宋体; font-size: 14px; text-indent: 30px; white-space: normal;\">法语名：</strong><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">panda géant</span></span></span></span></p><h4>分类地位</h4><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\">界： 动物界 Animalia</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\">门： 脊索动物门 Chordata</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\">纲： 哺乳纲 Mammalia</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\">目： 食肉目 Carnivora</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\">科： 熊科 Ursidae</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\">属： 大熊猫属 Ailuropoda</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\">种： 大熊猫 A. melanoleuca</p><h4>外形特征</h4><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">大熊猫体型肥硕似熊，憨态可掬，但头圆尾短，头部和身体毛色黑白相间分明。其体长120~180厘米，尾长10~20厘米，白色，体重60~110千克。头圆而大，前掌除了5个带爪的趾外，还有一个第六趾。躯干是尾白色，两耳、眼周、四肢和肩胛部全是黑色，腹部淡棕色或灰黑色。</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">生活在陕西秦岭的大熊猫因头部更圆而更像猫，被誉为国宝中的“美人”。</span></span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\"><br/></span></span></p><h4 style=\"text-indent: 0em;\">分布范围</h4><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">大熊猫生活在中国西南青藏高原东部边缘的温带森林中，竹子是这里主要的林下植物。我国长江上游向青藏高原过渡的这一系列高山深谷地带，包括秦岭、岷山、邛崃山、大小相岭和大小凉山等山系。秦岭山系分布于南麓，主要分布的县是佛坪，一般分布的县是洋县，仅有少量分布的县有太白、宁陕、周至、留坝、宁强等。岷山系除甘肃 文县为一般分布外，其余都分布于四川。在四川主要分布的县有平武、青川和北川等三县，一般分布的有九寨沟县、松潘、茂县等县，少量分布的县有安县、绵竹、彭州、什邡和都江堰市。邛崃山系主要分布的县有宝兴、汶川和天全等，一般分布的县有大邑和芦山，仅少量分布的县有邛崃、崇州、康定、泸定等。大小相岭除洪雅和冕宁为一般分布县外，仅有少量分布的县有荥经、石棉、汉源、九龙等。大小凉山除马边、美姑和越西有一定数量外，仅有少量分布的县有甘洛、峨边和雷波等。</span></p><h4 style=\"text-indent: 0em;\"><span style=\"font-size: 18px;\">生活环境</span></h4><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">大熊猫栖息于长江上游各山系的高山深谷，为东南季风的迎风面，气候温凉潮湿，其湿度常在80%以上，故它们是一种喜湿性动物。它们活动的区域多在坳沟、山腹洼地、河谷阶地等，一般在20°以下的缓坡地形。这些地方土质肥厚，森林茂盛，箭竹生长良好，构成为一个气温相对较为稳定、隐蔽条件良好、食物资源和水源都很丰富的优良食物基地。居住于海拔2400~3500米的高山竹林中。其生活环境湿度很大，温差也比较大。</span></p><h4 style=\"text-indent: 0em;\"><span style=\"font-size: 18px;\">生活习性</span></h4><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">除发情期外，常过着独栖生活，昼夜兼行。巢域面积为3.9~6.4km2(抄时写成平方千米）不定，个体之间巢域有重叠现象，雄体的巢域略大于雌体。雌体大多数时间仅活动于30~40公顷的核域内，雌体间的核域不重叠。食物主要是高山、亚高山上的50种竹类，偶食其它植物，甚至动物尸体。日食量很大，每天还到泉水或溪流饮水。</span></p><h4 style=\"text-indent: 0em;\"><span style=\"font-size: 18px;\">濒危等级</span></h4><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">IUCN：濒危</span></p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">国家重点保护等级： 一级</span></p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">中国濒危动物红皮书等级： 濒危</span></p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">中国的国宝动物</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\"><strong style=\"margin: 0px; padding: 0px; font-family: 宋体; font-size: 14px; text-indent: 30px; white-space: normal;\"><br/></strong></span><br/></p>', 'http://localhost:8081/animal/upload/image/20200901/1598899066797048620.png', '1010');
INSERT INTO `type_details` VALUES ('123457', '华南虎', '<p><img src=\"http://localhost:8081/animal/upload/image/20200901/1598939716772019863.jpg\" title=\"1598939716772019863.jpg\" alt=\"tiger.jpg\"/></p><h3>华南虎</h3><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\"><strong style=\"margin: 0px; padding: 0px;\">拉丁文学名：</strong>Panthera tigris amoyensis</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\"><strong style=\"margin: 0px; padding: 0px;\">英文名：</strong>south China tiger</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\"><strong style=\"margin: 0px; padding: 0px;\">中文名：</strong>华南虎</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\"><strong style=\"margin: 0px; padding: 0px;\">中文别名：</strong>厦门虎，南中国虎，中国虎</p><h3>分类地位：</h3><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">界： 动物界 Animalia</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">门： 脊索动物门 Chordata</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">亚门： 脊椎动物亚门Vertebrata</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">纲： 哺乳纲 Mammalia</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">目： 食肉目 Carnivora</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">科： 猫科 Felidae</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">属： 豹属 Panthera</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">种： 虎 P. tigris</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">亚种： 华南虎 P. t. amoyensis</p><h3>外形特征</h3><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">华南虎亦称“中国虎”，是中国特有的虎种，生活在中国中南部。识别特点： 头圆，耳短，四肢粗大有力，尾较长，胸腹部杂有较多的乳白色,全身橙黄色并布满黑色横纹。是亚种老虎中体型最小的。雄虎身长约2.5米（加头），重约150公斤。雌虎身长约2.3米，体重约110公斤。尾长80-100厘米。毛皮上有既短又窄的条纹，条纹的间距较孟加拉虎、西伯利亚虎的大,体侧还常出现菱形纹。</span></p><h3 style=\"text-indent: 0em;\">生活习性<br/></h3><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">主要生活在森林山地。多单独生活，不成群，多在夜间活动，嗅觉发达，行动敏捷，善于游泳，但不能爬树。以草食性动物野猪、鹿、狍等为食。一般来说，一只老虎的生存至少需要70平方公里的森林，还必须生存有200只梅花鹿、300只羚羊和150只野猪。</span></p><p style=\"text-indent: 0em;\"><br/></p><h3 style=\"text-indent: 0em;\">分布范围<br/></h3><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">该亚种曾广泛分布于华东、华中、华南、西南的广阔地区，以及陕西、陇东、豫西和晋南的个别地区（湖南、江西、贵州、福建、广东、广西、浙江、湖北、四川、河南、陕西、山西、甘肃等地</span></p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">原始分布区大致为（东西超过2000公里 南北超过1500公里）：</span></p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">东 东经1190~1200（浙闽边界）</span></p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">西 东经1000左右（青川边界）</span></p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">南 北纬210~350（秦岭至黄河一线）</span></p><h3 style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 0em;\"><br/></h3><h3>保护级别</h3><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">华南虎是虎所有种类中最为濒临灭绝的一种，估计世界上少于20至30只野生华南虎，在中国的19个动物园中还有70只圈养的华南虎，为国家一级保护动物。1996年被国际自然保护联盟列为极度濒危的十大物种之一。 于1981年被列入CITES公约附录Ⅰ保护名单。</span></p>', 'http://localhost:8081/animal/upload/image/20200901/1598939716772019863.jpg', '1010');
INSERT INTO `type_details` VALUES ('123458', '拦路虎', '<p><img src=\"http://localhost:8081/animal/upload/image/20200901/1598954789418083760.png\" title=\"1598954789418083760.png\" alt=\"1-5.png\"/></p><h3>华南虎</h3><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\"><strong style=\"margin: 0px; padding: 0px;\">拉丁文学名：</strong>Panthera tigris amoyensis</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\"><strong style=\"margin: 0px; padding: 0px;\">英文名：</strong>south China tiger</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\"><strong style=\"margin: 0px; padding: 0px;\">中文名：</strong>华南虎</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\"><strong style=\"margin: 0px; padding: 0px;\">中文别名：</strong>厦门虎，南中国虎，中国虎</p><h3>分类地位：</h3><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">界： 动物界 Animalia</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">门： 脊索动物门 Chordata</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">亚门： 脊椎动物亚门Vertebrata</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">纲： 哺乳纲 Mammalia</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">目： 食肉目 Carnivora</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">科： 猫科 Felidae</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">属： 豹属 Panthera</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">种： 虎 P. tigris</p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 2em;\">亚种： 华南虎 P. t. amoyensis</p><h3>外形特征</h3><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">华南虎亦称“中国虎”，是中国特有的虎种，生活在中国中南部。识别特点： 头圆，耳短，四肢粗大有力，尾较长，胸腹部杂有较多的乳白色,全身橙黄色并布满黑色横纹。是亚种老虎中体型最小的。雄虎身长约2.5米（加头），重约150公斤。雌虎身长约2.3米，体重约110公斤。尾长80-100厘米。毛皮上有既短又窄的条纹，条纹的间距较孟加拉虎、西伯利亚虎的大,体侧还常出现菱形纹。</span></p><h3 style=\"text-indent: 0em;\">生活习性<br/></h3><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">主要生活在森林山地。多单独生活，不成群，多在夜间活动，嗅觉发达，行动敏捷，善于游泳，但不能爬树。以草食性动物野猪、鹿、狍等为食。一般来说，一只老虎的生存至少需要70平方公里的森林，还必须生存有200只梅花鹿、300只羚羊和150只野猪。</span></p><p style=\"text-indent: 0em;\"><br/></p><h3 style=\"text-indent: 0em;\">分布范围<br/></h3><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">该亚种曾广泛分布于华东、华中、华南、西南的广阔地区，以及陕西、陇东、豫西和晋南的个别地区（湖南、江西、贵州、福建、广东、广西、浙江、湖北、四川、河南、陕西、山西、甘肃等地</span></p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">原始分布区大致为（东西超过2000公里 南北超过1500公里）：</span></p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">东 东经1190~1200（浙闽边界）</span></p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">西 东经1000左右（青川边界）</span></p><p style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 30px;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">南 北纬210~350（秦岭至黄河一线）</span></p><h3 style=\"margin: 0px 3px 15px; padding: 0px; font-family: 宋体; font-size: 14px; white-space: normal; text-indent: 0em;\"><br/></h3><h3>保护级别</h3><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 14px; text-indent: 30px;\">华南虎是虎所有种类中最为濒临灭绝的一种，估计世界上少于20至30只野生华南虎，在中国的19个动物园中还有70只圈养的华南虎，为国家一级保护动物。1996年被国际自然保护联盟列为极度濒危的十大物种之一。 于1981年被列入CITES公约附录Ⅰ保护名单。</span></p>', 'http://localhost:8081/animal/upload/image/20200901/1598954789418083760.png', '1014');
